var darkSwitch = document.getElementById("darkSwitch");
window.addEventListener("load", (function () {
    if (darkSwitch) {
        initTheme();
        darkSwitch.addEventListener("change", (function () {
            resetTheme()
        }))
    }
}));

function initTheme() {
    var darkThemeSelected = localStorage.getItem("darkSwitch") !== null && localStorage.getItem("darkSwitch") === "dark";
    darkSwitch.checked = darkThemeSelected;
    if (darkThemeSelected) {
        document.documentElement.setAttribute("data-bs-theme", "dark");
        document.getElementById("darkSwitch").labels[0].innerHTML = "<i class=\"bi bi-moon-stars\"></i>"
    } else {
        document.documentElement.removeAttribute("data-bs-theme");
        document.getElementById("darkSwitch").labels[0].innerHTML = "<i class=\"bi bi-brightness-high\"></i>"
    }
    // darkThemeSelected ? document.documentElement.setAttribute("data-bs-theme", "dark") : document.documentElement.removeAttribute("data-bs-theme")
}

function resetTheme() {
    if (darkSwitch.checked) {
        document.documentElement.setAttribute("data-bs-theme", "dark");
        document.getElementById("darkSwitch").labels[0].innerHTML = "<i class=\"bi bi-moon-stars\"></i>";
        localStorage.setItem("darkSwitch", "dark");
    } else {
        document.documentElement.removeAttribute("data-bs-theme");
        document.getElementById("darkSwitch").labels[0].innerHTML = "<i class=\"bi bi-brightness-high\"></i>";
        localStorage.removeItem("darkSwitch");
    }
}